package org.zegai.leetcode.easy;

import java.util.HashMap;
import java.util.Map;

public class FindTheDifference {

    public static void main(String[] args) {
        System.out.println(new FindTheDifference().findTheDifference("abcd", "abcde"));
        System.out.println(new FindTheDifference().findTheDifference("abcd", "abxcd"));
        System.out.println(new FindTheDifference().findTheDifference("", "y"));
        System.out.println(new FindTheDifference().findTheDifference(
                "",
                ""));
    }

    public char findTheDifference(String s, String t) {
        if(s.isEmpty()) {
            return t.charAt(0);
        }

        Map<Character, Integer> map = new HashMap<>();

        for(char c: s.toCharArray()) {
            map.put(c, map.getOrDefault(c, 0) + 1);
        }

        for(char c: t.toCharArray()) {
            if(map.getOrDefault(c, 0) == 0) {
                return c;
            }
            map.put(c, map.get(c) - 1);
        }

        return t.charAt(t.length() - 1);
    }
}
